import React, { Component } from "react";
import "../App.css";

class TwitForm extends Component {
  state = {
    user: "",
    message: "",
    created_at: "",
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    // 이 컴포넌트의 props에 onCreate를 부여하고,
    // 그 props에 묶이는 함수에 this.state 인자로 전달
    this.props.onCreate(this.state);

    // state 초기화
    this.setState({
      user: "",
      message: "",
      created_at: "",
    });
  };

  render() {
    return (
      <div id="twit-form">
        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="Please input you name"
            value={this.state.user}
            name="user"
            onChange={this.handleChange}
          />
          <input
            placeholder="Twit your Twit"
            maxLength="140"
            value={this.state.message}
            name="message"
            onChange={this.handleChange}
          />
          <button type="submit">register twit</button>
        </form>
      </div>
    );
  }
}

export default TwitForm;
